import React, {useState} from "react";
import { Link } from "react-router-dom";
import { addFav, removeFav } from "../redux/actions";
import { connect } from "react-redux";
import "../hojas-de-estilo/Card.css";

function Card({ id, name, species, gender, image, onClose, addFav, removeFav }) {
  const [isFav, setIsFav] = useState(false);
  const handleFavorite = () => {
    if(isFav){
      setIsFav(false);
      removeFav(id);
    }else{
      setIsFav(true);
      addFav({id, name, species, gender, image});
    }
  }
  return (
    <div className="card-container">
      <div className="boton">
        <button className="cerrar" onClick={() => onClose(id)}>
          {" "}
          X{" "}
        </button>
      </div>
      <Link to={`/detail/${id}`}>
        <h2 className="nombre">{name}</h2>
      </Link>
      <img className="imagen" src={image} alt={name} />
      <div className="pie-de-pagina">
        <h2 className="datos">{species}</h2>
        <h2 className="datos">{gender}</h2>
      </div>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    addFav: (personaje) => dispatch(addFav(personaje)),
    removeFav: (id) => dispatch(removeFav(id)),
  };
};
export default connect(null, mapDispatchToProps)(Card);
